ValidationRule.errorConditionFormula:
AND(
  OR(
     $Profile.Name = 'Apollo: Custom- Sales_China Profile',
     $Profile.Name = 'Apollo: Custom- Sales Profile',
     $Profile.Name = 'Apollo: Custom- Sales Engineers Profile',
     $Profile.Name = 'Apollo: Custom- Renewals Profile'
  ),  
  OR(
     AND(RecordType.DeveloperName = 'New_Business', NOT(ISPICKVAL(StageName, 'Accepted'))),
     AND(RecordType.DeveloperName = 'Renewal', NOT(ISPICKVAL(StageName, 'Not Contacted')))
  ),
  OR(
     AND(
      NOT(ISBLANK(Distributor__c)), NOT(ISBLANK(Distributor_Contact__c)), Distributor_Contact__r.AccountId <> Distributor__c
     ),
     AND(
      NOT(ISBLANK(Reseller__c)), NOT(ISBLANK(Reseller_Contact__c)), Reseller_Contact__r.AccountId <> Reseller__c
     )
  )
)
